    CASE WHEN user_id IS NOT NULL THEN 1 ELSE 0 END as existe
  FROM (
    SELECT user_id, user_name, chat_id, is_active, last_seen
    FROM telegram_users 
    WHERE user_id = $1
    UNION ALL
    SELECT NULL::BIGINT, NULL::VARCHAR, NULL::BIGINT, NULL::BOOLEAN, NULL::TIMESTAMP
    WHERE NOT EXISTS (SELECT 1 FROM telegram_users WHERE user_id = $1)
  ) t
  LIMIT 1
`;

