// FUNCIÓN PARA CONVERTIR UTC A HORA LOCAL ESPAÑOLA
const convertirUTCaHoraLocal = (fechaString) => {
  if (!fechaString) return '00:00';

  try {
    // Crear fecha UTC desde el string de Odoo
    const fechaUTC = new Date(fechaString + 'Z'); // Agregar Z para forzar UTC

    // Convertir a hora española (UTC+1 en invierno, UTC+2 en verano)
    const horaLocal = fechaUTC.toLocaleTimeString('es-ES', {
      hour: '2-digit',
      minute: '2-digit',
      timeZone: 'Europe/Madrid'
    });

    console.log(`🕐 Conversión: ${fechaString} (UTC) → ${horaLocal} (Madrid)`);
    return horaLocal;

  } catch (error) {
    console.log('❌ Error convirtiendo hora:', error);
    return '00:00';
  }
};

// Formatear fecha
const fechaFormateada = new Date(fecha).toLocaleDateString('es-ES', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// Generar mensaje de resumen
let resumen = `🌅 **Buenos días! Resumen para ${fechaFormateada}**\n\n`;

// SECCIÓN: EVENTOS DEL DÍA
resumen += `📅 **AGENDA DEL DÍA**\n`;
if (eventosDelDia.length === 0) {
  resumen += `✅ No hay eventos programados para hoy\n\n`;
} else {
  // Agrupar eventos por usuario
  const eventosPorUsuario = {};

  eventosDelDia.forEach(evento => {
    const usuario = evento.user_id?.[1] || 'Sin asignar';
    if (!eventosPorUsuario[usuario]) {
      eventosPorUsuario[usuario] = [];
    }
    eventosPorUsuario[usuario].push(evento);
  });

  Object.keys(eventosPorUsuario).forEach(usuario => {
    resumen += `👤 **${usuario}:**\n`;
    eventosPorUsuario[usuario].forEach(evento => {
      // CONVERTIR UTC A HORA LOCAL ESPAÑOLA
      const horaInicio = convertirUTCaHoraLocal(evento.start);
      const horaFin = convertirUTCaHoraLocal(evento.stop);

      resumen += `   🕐 ${horaInicio}-${horaFin}: ${evento.name}`;
      if (evento.location) {
        resumen += ` 📍 ${evento.location}`;
      }
      resumen += `\n`;
    });
    resumen += `\n`;
  });
}

// SECCIÓN: ÓRDENES PENDIENTES
resumen += `🛒 **ÓRDENES PENDIENTES DE CONFIRMAR**\n`;
if (ordenesPendientes.length === 0) {
  resumen += `✅ No hay órdenes pendientes\n\n`;
} else {
  const totalPendiente = ordenesPendientes.reduce((acc, orden) => acc + (orden.amount_total || 0), 0);
  resumen += `📊 **${ordenesPendientes.length} órdenes** por €${totalPendiente.toFixed(2)}\n\n`;

  // Mostrar máximo 10 órdenes para no hacer el mensaje muy largo
  const ordenesAMostrar = ordenesPendientes.slice(0, 10);

  ordenesAMostrar.forEach((orden, idx) => {
    const fechaOrden = new Date(orden.date_order).toLocaleDateString('es-ES');
    const estadoTexto = orden.state === 'draft' ? '📝 Borrador' : '📨 Enviado';

    resumen += `${idx + 1}. **${orden.name}**\n`;
    resumen += `   👤 ${orden.partner_id?.[1] || 'Cliente no identificado'}\n`;
    resumen += `   💰 €${orden.amount_total?.toFixed(2) || '0.00'} | ${estadoTexto} | 📅 ${fechaOrden}\n`;

    // Mostrar fecha de validez si existe
    if (orden.validity_date) {
      const fechaValidez = new Date(orden.validity_date).toLocaleDateString('es-ES');
      resumen += `   ⏰ Válido hasta: ${fechaValidez}\n`;
    }

    resumen += `\n`;
  });

  if (ordenesPendientes.length > 10) {
    resumen += `📋 _Y ${ordenesPendientes.length - 10} órdenes más..._\n\n`;
  }
}

// FOOTER
resumen += `⚡ _Resumen automático generado a las ${new Date().toLocaleTimeString('es-ES')}_`;

return {
  texto: resumen,
  eventos_count: eventosDelDia.length,
  ordenes_count: ordenesPendientes.length,
  fecha: fecha
};

