  eventosPersonalizados.forEach((evento, index) => {
    console.log('🔍 Procesando evento:', evento.name, 'Start:', evento.start, 'Stop:', evento.stop);

    // CONVERTIR UTC A HORA LOCAL ESPAÑOLA
    const inicio = convertirUTCaHoraLocal(evento.start);
    const fin = convertirUTCaHoraLocal(evento.stop);

    seccionEventos += `${index + 1}. **${evento.name}**\n`;
    seccionEventos += `   🕐 ${inicio} - ${fin}`;

    if (evento.location) {
