  sale_id: saleId,
};

const pedidoCreado = $('HTTP Crear Pedido').item.json.result; // ID
const readResult = $json.result; // resultado de Leer Pedido
const orderName = Array.isArray(readResult) && readResult[0]?.name
  ? readResult[0].name
  : pedidoCreado.toString();

const datos = $('Crear Pedido Venta').item.json;
const clienteDatos = datos.cliente_datos;
const lineasPedido = datos.lineas_pedido;
const productosNoEncontrados = datos.productos_no_encontrados;
const baseUrl = 'https://lsanchezgoshawk-n8npruebas.odoo.com';

let texto = '';

if (!pedidoCreado) {
  texto = '❌ Error al crear el pedido de venta. Por favor, intenta de nuevo.';
} else {
  texto = `✅ **¡Pedido de venta creado exitosamente!**\n\n`;
  texto += `🧾 **Nº Pedido:** ${orderName}\n`;
  texto += `🛒 **ID Interno:** ${pedidoCreado}\n`;
  const enlace = `${baseUrl}/web#id=${pedidoCreado}&model=sale.order&view_type=form`;
  texto += `🔗 **Pedido:** [Abrir en Odoo](${enlace})\n`;
  texto += `👤 **Cliente:** ${clienteDatos.name}\n\n`;

  texto += `📦 **Productos incluidos:**\n`;
  for (const linea of lineasPedido) {
    texto += `• ${linea.name} - Cantidad: ${linea.product_uom_qty} - Precio: €${linea.price_unit}\n`;
  }

  if (productosNoEncontrados.length > 0) {
    texto += `\n⚠️ **Productos no encontrados:**\n`;
    for (const producto of productosNoEncontrados) {
      texto += `• ${producto}\n`;
    }
  }

  texto += `\n🎉 ¡El pedido está listo!`;
}

return {
  texto,
  chat_id: datos.chat_id,
  pedido_id: pedidoCreado,
  pedido_numero: orderName,
  cliente_id: datos.cliente_id,
  productos_incluidos: lineasPedido.length,
  productos_no_encontrados: productosNoEncontrados.length
};

const saleId = $json.result; // ID que devuelve el create
const uid = $('Crear Pedido Venta').item.json.uid;

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      'lsanchezgoshawk-bot-main-27977821',
      uid,
      'admin',
      'sale.order',
      'read',
      [[saleId], ['name']]
    ],
  },
  id: Math.floor(Math.random() * 1e6),
};

return {
  payload,
  sale_id: saleId,
  uid,
};

const fromCreate = $('Crear Pedido Venta').item.json;
const fromRespuesta = $('Formatear Respuesta Pedido').item.json;

const chat_id = fromCreate.chat_id;
const user_id = $('Router de Acciones FIXED').item.json.user_id || chat_id;
const cliente_id = fromCreate.cliente_id || null;
const cliente_nombre = fromCreate.cliente_datos?.name || '';
const pedido_id = fromRespuesta.pedido_id;
const pedido_numero = fromRespuesta.pedido_numero;
const lineas = fromCreate.lineas_pedido || [];

return {
  chat_id,
  user_id,
  cliente_id,
  cliente_nombre,
  pedido_id,
  pedido_numero,
  lineas_json: JSON.stringify(lineas),
};

// Entrada del workflow (lo que le pasamos con Execute Workflow)
const input = $json;

// Mensaje que ha escrito el usuario
const msg = input.mensaje_original || input.text || '';

// Intentar detectar algo tipo "S00015", "SO0123", etc.
const mName = msg.match(/([A-Z]{1,3}\d{3,})/i);

// Intentar detectar un número "123", "2345", etc.
const mId = msg.match(/\b(\d{3,})\b/);

let numero_pedido = null;

if (mName) {
  numero_pedido = mName[1];
} else if (mId) {
  numero_pedido = mId[1];
}

// Devolvemos todo lo que nos llegue + el numero_pedido detectado
return {
  ...input,
  numero_pedido,
};

// Aquí $json YA es el pedido, no un array
const pedido = $json;

const lineas = pedido.lineas || [];
const numeroPedido = pedido.pedido_numero || pedido.pedido_id;

let texto = `🧾 *Pedido ${numeroPedido}*\n`;
texto += `👤 Cliente: ${pedido.cliente_nombre || 'Desconocido'}\n\n`;
texto += `📦 *Productos:*\n`;

for (const linea of lineas) {
  texto += `• ${linea.name} — Cantidad: ${linea.product_uom_qty}\n`;
}

texto += `\n¿Qué deseas modificar?\n`;
texto += `Puedes decir cosas como:\n`;
texto += `- "añade 2 tartas de queso"\n`;
texto += `- "quita 1 coca cola"\n`;
texto += `- "cambia tarta grande por tarta pequeña"\n`;

return {
  chat_id: pedido.chat_id,
  user_id: pedido.user_id,
  pedido_id: pedido.pedido_id,
  pedido_numero: numeroPedido,
  lineas,
  texto,
};

const input = $json;
const msgRaw = (input.mensaje_original || input.text || '').toString();
const msg = msgRaw.toLowerCase();

// 1) Detectar si es un CAMBIO ("cambia X por Y", "tarta cambialo por tarta de queso")
if (/cambi(a|ar|alo|alos|arlo)/.test(msg) || /\spor\s/.test(msg)) {
  let producto_origen_texto = '';
  let producto_destino_texto = '';
  let cantidad_destino = null;

  // patrón 1: "cambia X por Y"
  let m = msg.match(/cambi[a-z]*\s+(.+?)\s+por\s+(.+)/);
  if (m) {
    producto_origen_texto = m[1].trim();
    producto_destino_texto = m[2].trim();
  } else {
    // patrón 2: "X cambialo por Y"
    m = msg.match(/(.+?)\s+cambi[a-z]*\s+por\s+(.+)/);
    if (m) {
      producto_origen_texto = m[1].trim();
      producto_destino_texto = m[2].trim();
    }
  }

  // Extraer cantidad destino si viene tipo "tarta de queso x 2"
  let m2 = producto_destino_texto.match(/(.+?)\s*x\s*(\d+(?:[.,]\d+)?)/);
  if (m2) {
    producto_destino_texto = m2[1].trim();
    cantidad_destino = parseFloat(m2[2].replace(',', '.')) || null;
  }

  if (!producto_origen_texto || !producto_destino_texto) {
    return {
      ...input,
      necesita_aclaracion: true,
      texto_bot: 'Para cambiar un producto dime algo como: "cambia tarta grande por tarta de queso x 2".'
    };
  }

  return {
    ...input,
    operacion: 'cambiar',
    necesita_aclaracion: false,
    producto_origen_texto,
    producto_destino_texto,
    cantidad_destino,
  };
}

// 2) Si no es cambio, mirar si es añadir / quitar / eliminar

let operacion = null;
let tipo_ajuste = null;

// ¿Hay algún número en el mensaje?
const tieneNumero = /\d+(?:[.,]\d+)?/.test(msg);

// AÑADIR
if (/(añad|anad|agreg|pon|mete|sum[ae])/i.test(msg)) {
  operacion = 'ajustar';
  tipo_ajuste = 'aumentar';

// QUITAR (quitar/restar cantidad, no eliminar del todo)
} else if (/(quit|borr|sac[ao])/i.test(msg)) {
  operacion = 'ajustar';
  tipo_ajuste = 'reducir';

// ELIMINAR
} else if (/elimin/i.test(msg)) {
  if (tieneNumero) {
    // "elimina 2 clavos" → reducir 2
    operacion = 'ajustar';
    tipo_ajuste = 'reducir';
  } else {
    // "elimina el producto clavos" / "elimina todas las lineas de clavos"
    // → eliminar la línea completa
    operacion = 'eliminar_linea';
    tipo_ajuste = 'eliminar_linea';
  }
}

if (!operacion) {
  // No entendemos qué hacer
  return {
    ...input,
    necesita_aclaracion: true,
    texto_bot: 'No he entendido qué quieres hacer. Dime por ejemplo: "añade 2 tartas de queso", "quita 1 coca cola" o "cambia tarta grande por tarta de queso".'
  };
}

// Extraer cantidad y nombre de producto para añadir/quitar/eliminar

let cantidad = 1;
let producto_texto = '';

// patrón "<verbo> 2 tarta de queso"
let m = msg.match(/(?:añad|anad|agreg|pon|mete|sum[ae]|quit|elimin|borr|sac[ao])[a-z]*\s+(\d+(?:[.,]\d+)?)\s+(.+)/);
if (m) {
  cantidad = parseFloat(m[1].replace(',', '.')) || 1;
  producto_texto = m[2].trim();
} else {
  // patrón "tarta de queso x 2"
  m = msg.match(/(.+?)\s*x\s*(\d+(?:[.,]\d+)?)/);
  if (m) {
    producto_texto = m[1].trim();
    cantidad = parseFloat(m[2].replace(',', '.')) || 1;
  } else {
    // sin cantidad: "<verbo> tarta de queso" / "elimina el producto clavos"
    producto_texto = msg
      .replace(/(?:añad|anad|agreg|pon|mete|sum[ae]|quit|elimin|borr|sac[ao])[a-z]*/g, '')
      .trim();
    cantidad = 1;
  }
}

if (!producto_texto) {
  return {
    ...input,
    necesita_aclaracion: true,
    texto_bot: 'Necesito que me digas el producto. Ejemplo: "añade 2 tartas de queso" o "quita 1 coca cola".'
  };
}

return {
  ...input,
  operacion,         // 'ajustar' o 'eliminar_linea'
  tipo_ajuste: tipo_ajuste || 'aumentar',
  cantidad,
  producto_texto,
  necesita_aclaracion: false
};

const item = $input.first().json;

return [{
  numero_pedido: item.numero_pedido,
  chat_id: item.chat_id,
  query_params: [item.numero_pedido, item.chat_id]
}];

const {
  pedido_id,
  pedido_numero,
  lineas = [],
  tipo_ajuste,   // 'aumentar' | 'reducir' | 'eliminar_linea'
  cantidad,
  producto_texto,
  chat_id,
  user_id,
  operacion,     // 'ajustar' | 'eliminar_linea' | 'cambiar' (lo manda el nodo anterior)
} = $json;

if (!pedido_id || !Array.isArray(lineas) || lineas.length === 0) {
  return {
    ...$json,
    error_modificacion: true,
    texto_bot: 'No tengo los datos del pedido para modificarlo.'
  };
}

// Normaliza texto (sin tildes, minúsculas, espacios simples)
const normalize = (s) => (s || '').toString()
  .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
  .toLowerCase().replace(/\s+/g, ' ')
  .trim();

const target = normalize(producto_texto || '');

// 1) Buscar coincidencias en las líneas del pedido
const candidatos = [];
for (let i = 0; i < lineas.length; i++) {
  const l = lineas[i];
  const nameNorm = normalize(l.name);
  if (!nameNorm) continue;

  let score = 0;
  if (nameNorm.includes(target) || target.includes(nameNorm)) {
    score = 3;
  } else {
    const t1 = new Set(nameNorm.split(' '));
    const t2 = new Set(target.split(' '));
    let inter = 0;
    for (const t of t1) if (t2.has(t)) inter++;
    if (inter > 0) score = 1;
  }

  if (score > 0) {
    candidatos.push({ index: i, linea: l, score });
  }
}

if (candidatos.length === 0) {
  // No está en el pedido → lo tratamos como producto nuevo a añadir
  return {
    ...$json,
    es_producto_nuevo: true,
    error_modificacion: false,
    texto_bot: null
  };
}

if (candidatos.length > 1) {
  // Varias líneas parecidas → desambiguar
  const textoOpciones = candidatos
    .map((c, idx) => `${idx + 1}. ${c.linea.name} — Cantidad: ${c.linea.product_uom_qty}`)
    .join('\n');

  return {
    ...$json,
    necesita_desambiguacion_linea: true,
    candidatos_linea: candidatos,
    texto_bot: `He encontrado varios productos que se parecen a "${producto_texto}":\n\n${textoOpciones}\n\nPor favor, dime el número del producto que quieres modificar.`
  };
}

// Solo 1 línea candidata
const elegido = candidatos[0];
const idx = elegido.index;
const linea = { ...elegido.linea };
const nuevas_lineas = lineas.map(l => ({ ...l })); // copia shallow

const actual = Number(linea.product_uom_qty || 0);
const q = Number(cantidad || 1);

// 🧨 CASO NUEVO: eliminar la línea completa
// Lo marcamos así desde el nodo de interpretación cuando el usuario dice:
// "elimina el producto clavos", "elimina la línea de producto de clavos", etc.
if (operacion === 'eliminar_linea' || tipo_ajuste === 'eliminar_linea') {
  const nuevas_lineas_filtradas = nuevas_lineas.filter((_, i) => i !== idx);

  return {
    ...$json,
    error_modificacion: false,
    es_producto_nuevo: false,
    nuevas_lineas: nuevas_lineas_filtradas,
    texto_bot: null,       // El mensaje se formatea en el siguiente nodo
    linea_afectada: linea, // Por si quieres loguear qué se eliminó
  };
}

// AUMENTAR
if (tipo_ajuste === 'aumentar') {
  nuevas_lineas[idx].product_uom_qty = actual + q;
  return {
    ...$json,
    error_modificacion: false,
    es_producto_nuevo: false,
    nuevas_lineas,
    texto_bot: null, // lo formatearemos luego
    linea_afectada: linea
  };
}

// REDUCIR
if (tipo_ajuste === 'reducir') {
  if (q < actual) {
    // Simplemente bajamos cantidad
    nuevas_lineas[idx].product_uom_qty = actual - q;
    return {
      ...$json,
      error_modificacion: false,
      es_producto_nuevo: false,
      nuevas_lineas,
      texto_bot: null,
      linea_afectada: linea
    };
  } else {
    // Quiere quitar igual o más de las que hay → preguntar si eliminamos producto
    return {
      ...$json,
      error_modificacion: false,
      requiere_confirmar_eliminacion: true,
      linea_a_eliminar: linea,
      indice_linea_a_eliminar: idx,
      texto_bot: `Solo hay ${actual} unidades de "${linea.name}" en el pedido. ¿Quieres eliminar este producto del pedido? (responde "sí" o "no").`
    };
  }
}

return {
  ...$json,
  error_modificacion: true,
  texto_bot: 'Por ahora solo puedo añadir o quitar cantidad de productos del pedido.'
};

// Helper para leer json de otro nodo
function safeJson(nodeName) {
  try {
    const it = $items(nodeName, 0, 0);
    return (Array.isArray(it) && it[0] && it[0].json) ? it[0].json : null;
  } catch (e) {
    return null;
  }
}

// Preferimos los datos de Procesar Producto Nuevo si existen (producto añadido)
// Si no, usamos los de Aplicar Modificación (ajuste de línea existente)
const desdeNuevo = safeJson('Procesar Producto Nuevo') || {};
const desdeMod   = safeJson('Aplicar Modificación')   || {};

const datos = Object.keys(desdeNuevo).length ? desdeNuevo : desdeMod;

const nuevas_lineas   = datos.nuevas_lineas || datos.lineas || [];
const numeroPedido    = datos.pedido_numero || datos.pedido_id;
const clienteNombre   = datos.cliente_nombre || '';
const chatId          = datos.chat_id;
const userId          = datos.user_id;
const pedidoId        = datos.pedido_id;

let texto = `✅ *Pedido ${numeroPedido} actualizado*.\n\n`;
texto += `📦 *Productos actuales:*\n`;

for (const l of nuevas_lineas) {
  texto += `• ${l.name} — Cantidad: ${l.product_uom_qty}\n`;
}

texto += `\nSi quieres hacer otro cambio, puedes decirme por ejemplo:\n`;
texto += `- "añade 1 coca cola"\n`;
texto += `- "quita 2 tartas de queso"\n`;

return {
  chat_id: chatId,
  user_id: userId,
  pedido_id: pedidoId,
  pedido_numero: numeroPedido,
  cliente_nombre: clienteNombre,
  lineas: nuevas_lineas,
  texto,
};

