parameters.jsCode:
const {
  producto_texto,
  cantidad,
  pedido_id,
  pedido_numero,
  lineas = [],
  chat_id,
  user_id,
} = $json;

// UID de Odoo desde tu nodo de login
const uid = $('Autenticar Odoo').item.json.result;
const db = 'lsanchezgoshawk-bot-main-27977821';

if (!uid) {
  return {
    ...$json,
    error_producto_nuevo: true,
    texto_bot: 'No he podido autenticar con Odoo para añadir el producto nuevo.',
  };
}

const term = (producto_texto || '').toString().trim();

// Dominio simple: productos vendibles cuyo nombre contenga el texto
const domain = ['&', ['sale_ok', '=', true], ['name', 'ilike', term]];

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      db,
      uid,
      'admin',
      'product.product',
      'search_read',
      [domain],
      { fields: ['id', 'name', 'list_price', 'default_code'], limit: 20 },
    ],
  },
  id: Math.floor(Math.random() * 1e6),
};

return {
  uid,
  db,
  chat_id,
  user_id,
  pedido_id,
  pedido_numero,
  lineas,
  producto_texto: term,
  cantidad,
  payload,
};

// Nodo: Procesar Producto Nuevo

// 1) Respuesta pura de Odoo que viene del HTTP
const respuesta = $json;
const resultados = Array.isArray(respuesta.result) ? respuesta.result : [];

// 2) Recuperamos el contexto original del nodo "Preparar Búsqueda Producto Nuevo"
const original = $('Preparar Búsqueda Producto Nuevo').item.json;

const {
  producto_texto,
  cantidad,
  lineas = [],
  pedido_id,
  pedido_numero,
  chat_id,
  user_id,
  db,
  uid,
} = original;

// 3) Si NO hay resultados → pedir corrección
if (!resultados.length) {
  return {
    ...original,
    odoo_raw: respuesta,
    error_producto_nuevo: true,
    texto_bot:
      `No he encontrado ningún producto en Odoo que se parezca a "*${producto_texto}*".\n\n` +
      `Prueba a decirme el producto con otro nombre o más detalle.\n` +
      `Por ejemplo:\n` +
      `• "añade 2 tarta de queso grande"\n` +
      `• "tarta de queso entera x 2"`,
  };
}

// 4) Si hay varios resultados → mostrar opciones y pedir que aclare
if (resultados.length > 1) {
  const top = resultados.slice(0, 7); // máximo 7

  const opciones = top
    .map((p, idx) => {
      const ref = p.default_code || 'sin referencia';
      const precio = (p.list_price ?? 0).toString().replace('.', ',');
      return `${idx + 1}. ${p.name}  (ref: ${ref})  —  €${precio}`;
    })
    .join('\n');

  const texto_bot =
    `He encontrado varios productos que se parecen a "*${producto_texto}*":\n\n` +
    `${opciones}\n\n` +
    `Por favor, dime el producto correcto escribiendo su *nombre completo* y cantidad.\n\n` +
    `Ejemplos:\n` +
    `• "añade 2 ${resultados[0].name}"\n` +
    `• "${resultados[0].name} x 2"`;

  return {
    ...original,
