// Normalizamos y filtramos las líneas que ya había en el pedido
const lineas_validas = (lineas || [])
  .map(l => {
    const product_id = Number(l.product_id);
    const product_uom_qty = Number(l.product_uom_qty);
    const price_unit = Number(l.price_unit);

    return {
      name: l.name,
      product_id,
      product_uom_qty,
