let datos = {};

// Prioridad: lo que ya tiene lineasFinales desde el payload → luego nuevo → luego mod
if (Array.isArray(desdePayload.lineas) && desdePayload.lineas.length) {
  datos = desdePayload;
} else if (Object.keys(desdeNuevo).length) {
  datos = desdeNuevo;
} else {
  datos = desdeMod;
}

// Originales y parches (por si acaso queremos fusionar)
const originales = Array.isArray(datos.lineas) ? datos.lineas : [];
const parches    = Array.isArray(datos.nuevas_lineas) ? datos.nuevas_lineas : [];

// Misma función de key que en el otro nodo
function buildKey(l) {
  if (l.product_id && l.product_id !== 'id producto') {
    return String(l.product_id);
  }
  return (l.name || '')
    .toString()
    .toLowerCase()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .trim();
}

function mergeLineas(originales, parches) {
  if (!parches.length) return originales;

  const patchMap = new Map();
  for (const p of parches) {
    patchMap.set(buildKey(p), p);
  }

  const resultado = [];

  for (const orig of originales) {
    const key = buildKey(orig);
    if (patchMap.has(key)) {
      const patch = patchMap.get(key);
      const qtyPatch = Number(patch.product_uom_qty ?? patch.cantidad ?? 0) || 0;
      if (patch.eliminar === true || qtyPatch === 0) {
        patchMap.delete(key);
        continue;
      }
      resultado.push({ ...orig, ...patch });
      patchMap.delete(key);
    } else {
      resultado.push(orig);
    }
  }

  for (const patch of patchMap.values()) {
    const qtyPatch = Number(patch.product_uom_qty ?? patch.cantidad ?? 0) || 0;
    if (patch.eliminar === true || qtyPatch === 0) continue;
    resultado.push(patch);
  }

  return resultado;
}

let lineasFinales = [];

if (originales.length && parches.length) {
  lineasFinales = mergeLineas(originales, parches);
} else if (originales.length) {
  lineasFinales = originales;
} else {
  lineasFinales = parches;
}

// Si desde el nodo de payload ya venían lineas finales, las priorizamos
if (Array.isArray(desdePayload.lineas) && desdePayload.lineas.length) {
  lineasFinales = desdePayload.lineas;
}

