const clienteNombre = datos.cliente_nombre || '';
const chatId        = datos.chat_id;
const userId        = datos.user_id;

let texto = `✅ *Pedido ${numeroPedido} actualizado*.\n\n`;
texto += `📦 *Productos actuales:*\n`;

for (const l of lineasFinales) {
  const qty = Number(l.product_uom_qty ?? l.cantidad ?? 0) || 0;
  texto += `• ${l.name} — Cantidad: ${qty}\n`;
}

texto += `\nSi quieres hacer otro cambio, puedes decirme por ejemplo:\n`;
texto += `- "añade 1 coca cola"\n`;
texto += `- "quita 2 tartas de queso"\n`;

return {
  chat_id: chatId,
  user_id: userId,
  pedido_id: datos.pedido_id,
  pedido_numero: numeroPedido,
  cliente_nombre: clienteNombre,
  lineas: lineasFinales,
  texto,
};

