// PREPARAR PAYLOAD PARA ACTUALIZAR EL PEDIDO EN ODOO
// Este nodo mezcla las líneas originales con las nuevas
// y además filtra las líneas "fake" (id producto / precio texto).

// Helper para leer json de otro nodo sin romper si no existe
function safeJson(nodeName) {
  try {
    const it = $items(nodeName, 0, 0);
    return (Array.isArray(it) && it[0] && it[0].json) ? it[0].json : null;
  } catch (e) {
    return null;
  }
}

const cur = $json;

// 1) Clonamos el input base
let base = { ...cur };

// 2) Tomamos líneas originales + parches (nuevas_lineas)
let originales = Array.isArray(base.lineas) ? base.lineas : [];
let parches    = Array.isArray(base.nuevas_lineas) ? base.nuevas_lineas : [];

// Función para generar una clave de comparación por línea
function buildKey(l) {
  if (l.product_id && l.product_id !== 'id producto') {
    return String(l.product_id);
  }
  return (l.name || '')
    .toString()
    .toLowerCase()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .trim();
}

// Si no hay originales y sí hay parches, usamos parches como base
if (!originales.length && parches.length) {
  originales = parches;
}

// Fusionar líneas originales + nuevas (sin perder productos)
function mergeLineas(originales, parches) {
  if (!parches.length) return originales;

  const patchMap = new Map();
  for (const p of parches) {
    patchMap.set(buildKey(p), p);
  }

  const resultado = [];

  // 1) Aplicar parches sobre originales
  for (const orig of originales) {
    const key = buildKey(orig);
    if (patchMap.has(key)) {
      const patch = patchMap.get(key);

      const qtyPatch = Number(patch.product_uom_qty ?? patch.cantidad ?? 0) || 0;
      if (patch.eliminar === true || qtyPatch === 0) {
        patchMap.delete(key);
        continue; // eliminamos esta línea
      }

      resultado.push({
        ...orig,
        ...patch,
      });

      patchMap.delete(key);
    } else {
      // Línea sin cambios → se mantiene
      resultado.push(orig);
    }
  }

  // 2) Añadir líneas nuevas que no existían antes
  for (const patch of patchMap.values()) {
    const qtyPatch = Number(patch.product_uom_qty ?? patch.cantidad ?? 0) || 0;
    if (patch.eliminar === true || qtyPatch === 0) continue;

    resultado.push(patch);
  }

  return resultado;
}

const lineasFusionadas = mergeLineas(originales, parches);

// 3) Normalizar TODAS las líneas (para mostrar y guardar en memoria/DB)
const lineasNormalizadas = lineasFusionadas.map(l => {
  const pid   = l.product_id !== undefined ? Number(l.product_id) : NaN;
  const qty   = Number(l.product_uom_qty ?? l.cantidad ?? 0);
  const price = Number(l.price_unit ?? 0);

  return {
    ...l,
    // si product_id no es numérico, lo dejamos tal cual (para texto/memoria)
    product_id: Number.isFinite(pid) ? pid : l.product_id,
    product_uom_qty: Number.isFinite(qty) ? qty : 0,
    price_unit: Number.isFinite(price) ? price : 0,
  };
});

// 3b) Líneas válidas para Odoo (solo con product_id numérico)
const lineasValidas = lineasNormalizadas.filter(l => {
  const pid = Number(l.product_id);
  return Number.isInteger(pid) && pid > 0;
});

if (!lineasValidas.length) {
  // No hay ninguna línea con product_id numérico: NO intentamos escribir en Odoo.
  return {
    ...base,
    lineas: lineasNormalizadas,
    nuevas_lineas: lineasNormalizadas,
    error_modificacion: true,
    texto_bot: 'No tengo ninguna línea de producto con un ID válido para actualizar el pedido en Odoo.',
  };
}

// 4) Intentamos obtener uid y db
const auth = safeJson('Autenticar Odoo') || {};
const uid  = base.uid || auth.result;
const db   = base.db || 'lsanchezgoshawk-bot-main-27977821';

const pedidoId = base.pedido_id;

if (!pedidoId || !uid) {
  return {
    ...base,
    lineas: lineasNormalizadas,
    nuevas_lineas: lineasNormalizadas,
    error_modificacion: true,
    texto_bot: 'Faltan datos para actualizar el pedido en Odoo (uid o pedido_id).',
  };
}

// 4b) ¿Conocemos las líneas originales con product_id?
const originalesConId = (Array.isArray(originales) ? originales : []).filter(l => {
  const pid = Number(l.product_id);
  return Number.isInteger(pid) && pid > 0;
});

// Si NO hay product_id en las originales → solo añadimos nuevas líneas
const soloAppend = originalesConId.length === 0;

// 5) Construir los comandos order_line para Odoo
let order_line_commands;
if (soloAppend) {
  // No conocemos las líneas originales completas (sin product_id) → solo añadimos nuevas
  order_line_commands = lineasValidas.map(l => ([
