    0,
    {
      product_id: Number(l.product_id),
      product_uom_qty: l.product_uom_qty,
      price_unit: l.price_unit,
      name: l.name,
    },
  ]));
} else {
  // Tenemos snapshot completo → reescribimos todas las líneas
  order_line_commands = [
    [5, 0, 0], // limpia las líneas existentes en el pedido
    ...lineasValidas.map(l => ([
      0,
