      {
        product_id: Number(l.product_id),
        product_uom_qty: l.product_uom_qty,
        price_unit: l.price_unit,
        name: l.name,
      },
    ])),
  ];
}

// 6) Construir el payload JSON-RPC para sale.order.write
const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      db,
      uid,
      'admin',
      'sale.order',
      'write',
      [[pedidoId], { order_line: order_line_commands }],
    ],
  },
  id: Math.floor(Math.random() * 1e6),
};

return {
  ...base,
  uid,
